from dataclasses import dataclass

@dataclass
class Triple:
    subject: str
    predicate: str
    object: str

# EVOLVE-BLOCK-START

def predict(triples: list[Triple]) -> str:
    if not triples:
        return ""

    # Group triples by subject to create more coherent, complex sentences
    subject_groups = {}
    for triple in triples:
        subject = triple.subject
        if subject not in subject_groups:
            subject_groups[subject] = []
        subject_groups[subject].append(triple)

    sentences = []
    for subject, triples_list in subject_groups.items():
        # Build a rich description for each subject
        parts = []
        birth_info = None
        origin_info = None
        genre_info = None
        instrument_info = None
        active_years = None
        associated_artists = []
        record_label = None
        occupation = None
        background = None

        for triple in triples_list:
            predicate = triple.predicate
            obj = triple.object

            if predicate == "birthDate":
                birth_info = f"born on {obj}"
            elif predicate == "birthPlace":
                birth_info = f"born in {obj}" if not birth_info else f"{birth_info} in {obj}"
            elif predicate == "birthYear":
                birth_info = f"born in {obj}"
            elif predicate == "origin":
                origin_info = obj
            elif predicate == "genre":
                genre_info = obj
            elif predicate == "instrument":
                instrument_info = obj
            elif predicate == "activeYearsStartYear":
                active_years = f"active since {obj}"
            elif predicate == "activeYearsEndYear":
                active_years = f"active until {obj}"
            elif predicate == "associatedBand/associatedMusicalArtist":
                associated_artists.append(obj)
            elif predicate == "recordLabel":
                record_label = obj
            elif predicate == "occupation":
                occupation = obj
            elif predicate == "background":
                background = obj

        # Construct a complex sentence for this subject with improved grammar and flow
        parts = []

        # Add background
        if background:
            parts.append(f"has a background as a {background}")

        # Add birth information with proper ordering
        if birth_info:
            parts.append(birth_info)

        # Add origin
        if origin_info:
            parts.append(f"from {origin_info}")

        # Add genre
        if genre_info:
            parts.append(f"playing {genre_info} music")

        # Add instrument
        if instrument_info:
            parts.append(f"and plays the {instrument_info}")

        # Add active years
        if active_years:
            parts.append(active_years)

        # Add associated artists
        if associated_artists:
            artists_str = ", ".join(associated_artists)
            parts.append(f"associated with {artists_str}")

        # Add record label
        if record_label:
            parts.append(f"signed to {record_label}")

        # Add occupation
        if occupation:
            parts.append(f"and is a {occupation}")

        # Combine all parts with appropriate conjunctions
        description = f"{subject}"
        if parts:
            description += " " + ", ".join(parts)
        description += "."

        sentences.append(description)

    return " ".join(sentences)

