from dataclasses import dataclass

@dataclass
class Triple:
    subject: str
    predicate: str
    object: str

# EVOLVE-BLOCK-START

def predict(triples: list[Triple]) -> str:
    if not triples:
        return ""

    sentences = []
    for triple in triples:
        predicate = triple.predicate.replace("/", " ")
        if triple.predicate == "associatedBand/associatedMusicalArtist":
            sentences.append(f"{triple.subject} is associated with {triple.object}.")
        elif triple.predicate == "birthDate":
            sentences.append(f"{triple.subject} was born on {triple.object}.")
        elif triple.predicate == "genre":
            sentences.append(f"{triple.subject} plays {triple.object} music.")
        elif triple.predicate == "instrument":
            sentences.append(f"{triple.subject} plays the {triple.object}.")
        elif triple.predicate == "origin":
            sentences.append(f"{triple.subject} is from {triple.object}.")
        elif triple.predicate in ["activeYearsStartYear", "activeYearsEndYear"]:
            sentences.append(f"{triple.subject} active years are {triple.object}.")
        elif triple.predicate == "birthPlace":
            sentences.append(f"{triple.subject} was born in {triple.object}.")
        elif triple.predicate == "birthYear":
            sentences.append(f"{triple.subject} was born in {triple.object}.")
        elif triple.predicate == "occupation":
            sentences.append(f"{triple.subject} is a {triple.object}.")
        elif triple.predicate == "recordLabel":
            sentences.append(f"{triple.subject} is signed to {triple.object}.")
        elif triple.predicate == "deathPlace":
            sentences.append(f"{triple.subject} died in {triple.object}.")
        elif triple.predicate == "alternativeName":
            sentences.append(f"{triple.subject} is also known as {triple.object}.")
        elif triple.predicate in ["deathDate", "foundingDate"]:
            sentences.append(f"{triple.subject} died/founded on {triple.object}.")
        elif triple.predicate == "nationality":
            sentences.append(f"{triple.subject} is from {triple.object}.")
        elif triple.predicate == "professionalField":
            sentences.append(f"{triple.subject} works as a {triple.object}.")
        elif triple.predicate in ["musicFusionGenre", "musicSubgenre", "stylisticOrigin"]:
            sentences.append(f"{triple.subject} is categorized as {triple.object}.")
        elif triple.predicate in ["language", "officialLanguage"]:
            sentences.append(f"{triple.object} is spoken in {triple.subject}.")
        elif triple.predicate == "derivative":
            sentences.append(f"{triple.subject} derives from {triple.object}.")
        elif triple.predicate in ["location", "governingBody", "leader", "leaderTitle"]:
            sentences.append(f"{triple.subject} is related to {triple.predicate} {triple.object}.")
        elif triple.predicate in ["country", "ethnicGroup", "parentCompany", "bandMember", "capital", "anthem", "currency", "demonym", "isPartOf", "distributingCompany", "training", "populationDensity", "meaning", "longName", "areaTotal", "elevationAboveTheSeaLevel", "postalCode"]:
            sentences.append(f"{triple.subject} is related to {triple.predicate} {triple.object}.")
        else:
            # Handle unknown predicates with a more informative sentence
            sentence = f"Information about {triple.subject} and {triple.predicate} relating to {triple.object} is available."
            sentences.append(sentence)

    if len(sentences) == 1:
        return sentences[0]
    else:
        return " ".join(sentences)

