from dataclasses import dataclass

@dataclass
class Triple:
    subject: str
    predicate: str
    object: str

# EVOLVE-BLOCK-START

def predict(triples: list[Triple]) -> str:
    """
    Generates a natural language sentence from a list of triples.
    Groups facts by subject and constructs more coherent, complex sentences.
    """
    if not triples:
        return ""

    from collections import defaultdict
    subject_facts = defaultdict(list)

    for triple in triples:
        subject_facts[triple.subject].append((triple.predicate, triple.object))

    sentences = []
    for subject, facts in subject_facts.items():
        # Group facts by semantic categories with more nuanced handling
        birth_info = []
        career_info = []
        origin_info = []
        music_info = []
        other_info = []

        for pred, obj in facts:
            if pred in ("birthDate", "birthPlace", "birthYear"):
                if pred == "birthDate":
                    birth_info.append(f"on {obj}")
                elif pred == "birthPlace":
                    birth_info.append(f"in {obj}")
                elif pred == "birthYear":
                    birth_info.append(f"in {obj}")
            elif pred == "activeYearsStartYear":
                career_info.append(f"since {obj}")
            elif pred == "activeYearsEndYear":
                career_info.append(f"until {obj}")
            elif pred in ("origin", "nationality", "country", "isPartOf"):
                origin_info.append(obj)
            elif pred in ("genre", "musicSubgenre", "musicFusionGenre", "stylisticOrigin", "associatedBand/associatedMusicalArtist"):
                music_info.append(obj)
            elif pred in ("instrument", "background", "occupation", "professionalField", "training"):
                other_info.append(obj)
            elif pred == "deathDate":
                other_info.append(f"died on {obj}")
            elif pred == "deathPlace":
                other_info.append(f"died in {obj}")
            elif pred == "recordLabel":
                other_info.append(f"signed to {obj}")
            elif pred == "capital":
                other_info.append(f"with capital {obj}")
            elif pred == "currency":
                other_info.append(f"using {obj} as currency")
            elif pred == "anthem":
                other_info.append(f"with anthem {obj}")
            elif pred == "populationDensity":
                other_info.append(f"with population density of {obj}")
            elif pred == "areaTotal":
                other_info.append(f"with total area of {obj}")
            elif pred == "foundingDate":
                other_info.append(f"founded on {obj}")
            elif pred == "elevationAboveTheSeaLevel":
                other_info.append(f"elevated {obj} above sea level")
            elif pred == "postalCode":
                other_info.append(f"having postal code {obj}")
            elif pred == "language":
                other_info.append(f"where {obj} is spoken")
            elif pred == "officialLanguage":
                other_info.append(f"with {obj} as official language")
            elif pred == "derivative":
                other_info.append(f"derivative of {obj}")
            elif pred == "location":
                other_info.append(f"located in {obj}")
            elif pred == "governingBody":
                other_info.append(f"governed by {obj}")
            elif pred == "leader":
                other_info.append(f"led by {obj}")
            elif pred == "leaderTitle":
                other_info.append(f"holding title {obj}")
            elif pred == "ethnicGroup":
                other_info.append(f"inhabited by {obj}")
            elif pred == "parentCompany":
                other_info.append(f"subsidiary of {obj}")
            elif pred == "bandMember":
                other_info.append(f"featuring {obj}")
            elif pred == "demonym":
                other_info.append(f"people called {obj}")
            elif pred == "longName":
                other_info.append(f"also known as {obj}")
            elif pred == "meaning":
                other_info.append(f"meaning {obj}")
            else:
                other_info.append(obj)

        # Build coherent sentence parts with improved structure
        parts = []
        if birth_info:
            birth_str = "born " + ", ".join(birth_info)
            parts.append(birth_str)
        if career_info:
            career_str = "active " + " to ".join(career_info)
            parts.append(career_str)
        if origin_info:
            origin_str = ", ".join(origin_info)
            parts.append(f"from {origin_str}")
        if music_info:
            music_str = ", ".join(music_info)
            parts.append(f"performing {music_str} music")
        if other_info:
            other_str = ", ".join(other_info)
            parts.append(f"also known for {other_str}")

        # Combine parts into a single sentence with better flow
        if len(parts) == 1:
            sentence = f"{subject} {parts[0]}."
        elif len(parts) == 2:
            sentence = f"{subject} {parts[0]} and {parts[1]}."
        else:
            sentence = f"{subject} {', '.join(parts[:-1])} and {parts[-1]}."
        sentences.append(sentence)

    # Join multiple sentences with natural conjunctions
    if len(sentences) == 1:
        return sentences[0]
    elif len(sentences) == 2:
        return " and ".join(sentences)
    elif len(sentences) == 3:
        return ", ".join(sentences[:-1]) + ", and " + sentences[-1]
    else:
        return ", ".join(sentences)

