def predict(triples: list[Triple]) -> str:
    if not triples:
        return ""

    # Group triples by subject to enable sentence fusion
    subject_groups = {}
    for triple in triples:
        if triple.subject not in subject_groups:
            subject_groups[triple.subject] = []
        subject_groups[triple.subject].append(triple)

    sentences = []
    for subject, subject_triples in subject_groups.items():
        # Extract and group predicates for better sentence structure
