        if info["longName"]:
            parts.append(f"is officially known as {info['longName']}")

        # Meaning and alternative names
        if info["meaning"]:
            parts.append(f"means {info['meaning']}")
        if info["alternativeName"]:
            parts.append(f"is also known as {info['alternativeName']}")

        # If no specific information, fallback to generic sentence
        if not parts:
            # Use first triple for fallback if no categorized info
            first_triple = subject_triples[0]
            parts.append(f"The {first_triple.predicate} of {subject} is {first_triple.object}")

        sentences.append(f"{subject} {' and '.join(parts)}.")

    return " ".join(sentences)
