from dataclasses import dataclass

@dataclass
class Triple:
    subject: str
    predicate: str
    object: str

# EVOLVE-BLOCK-START

def predict(triples: list[Triple]) -> str:
    sentence = ""
    airport_name = None
    airport_info = []
    location_info = []
    country_info = []

    for triple in triples:
        if triple.predicate == "cityServed":
            airport_name = triple.subject
            airport_info.append(f"serves the city of {triple.object}")
        elif triple.predicate == "location":
            airport_info.append(f"is located in {triple.object}")
        elif triple.predicate == "elevationAboveTheSeaLevel":
            airport_info.append(f"which is {triple.object} metres above sea level")
        elif triple.predicate == "runwayLength":
            airport_info.append(f"and has a runway length of {triple.object} metres")
        elif triple.predicate == "country":
            country_info.append(f"in {triple.object}")
        elif triple.predicate == "isPartOf":
            location_info.append(f"which is part of {triple.object}")
        elif triple.predicate == "language":
            location_info.append(f"where the language spoken is {triple.object}")
        elif triple.predicate == "capital":
            country_info.append(f"where the capital is {triple.object}")

    if airport_name:
        sentence += f"{airport_name} "

    if airport_info:
        sentence += ", ".join(airport_info) + ", "

    if location_info:
        sentence += " ".join(location_info) + ", "

    if country_info:
        sentence += " ".join(country_info)

    return sentence.strip()

