from dataclasses import dataclass

@dataclass
class Triple:
    subject: str
    predicate: str
    object: str

# EVOLVE-BLOCK-START

def predict(triples: list[Triple]) -> str:
    subjects = {}
    for triple in triples:
        if triple.subject not in subjects:
            subjects[triple.subject] = []
        subjects[triple.subject].append((triple.predicate, triple.object))

    sentence = ""
    for subject, triples_list in subjects.items():
        subject_sentence = f"{subject}"
        for i, (predicate, obj) in enumerate(triples_list):
            if predicate == "cityServed":
                subject_sentence += f" serves the city of {obj}"
            elif predicate == "location":
                subject_sentence += f" is located in {obj}"
            elif predicate == "elevationAboveTheSeaLevel":
                subject_sentence += f" which is {obj} metres above sea level"
            elif predicate == "runwayLength":
                subject_sentence += f" and has a runway length of {obj} metres"
            elif predicate == "country":
                subject_sentence += f" in {obj}"
            elif predicate == "isPartOf":
                subject_sentence += f" which is part of {obj}"
            elif predicate == "language":
                subject_sentence += f" where the language spoken is {obj}"
            elif predicate == "leader":
                subject_sentence += f" led by {obj}"
            elif predicate == "currency":
                subject_sentence += f" with the currency {obj}"
            if i < len(triples_list) - 1:
                subject_sentence += ", "
        if sentence:
            sentence += f", {subject_sentence}"
        else:
            sentence += subject_sentence

    for triple in triples:
        if triple.predicate == "operatingOrganisation":
            sentence += f" and is operated by {triple.object}"
        elif triple.predicate == "owner":
            sentence += f" and is owned by {triple.object}"
        elif triple.predicate == "capital":
            sentence += f" where the capital is {triple.object}"
        elif triple.predicate == "demonym":
            sentence += f" with a demonym of {triple.object}"
        elif triple.predicate == "currency":
            sentence += f" with the currency {triple.object}"

    return sentence + "."

