from dataclasses import dataclass

@dataclass
class Triple:
    subject: str
    predicate: str
    object: str

# EVOLVE-BLOCK-START

def predict(triples: list[Triple]) -> str:
    predicates = {
        "cityServed": "serves the city of {}",
        "location": "is located in {}",
        "elevationAboveTheSeaLevel": "which is {} metres above sea level",
        "runwayLength": "and has a runway length of {} metres",
        "country": "in {}",
        "isPartOf": "which is part of {}",
        "language": "where the language spoken is {}",
        "capital": "where the capital is {}",
        "operatingOrganisation": "operated by {}",
        "nativeName": "also known as {}",
        "iataLocationIdentifier": "with the IATA code {}",
        "icaoLocationIdentifier": "and the ICAO code {}",
        "1stRunwaySurfaceType": "and has a 1st runway surface type of {}",
        "1stRunwayLengthFeet": "and has a 1st runway length of {} feet",
        "class": "which belongs to the class {}",
        "order": "and belongs to the order of {}",
        "elevationAboveTheSeaLevelInFeet": "which is {} feet above sea level",
        "elevationAboveTheSeaLevelInMetres": "which is {} metres above sea level",
        "areaCode": "with the area code {}",
        "postalCode": "with the postal code {}",
    }

    sentence = ""
    airport = None
    city = None
    country = None
    capital = None
    location = None
    elevation = None
    runway_length = None
    for triple in triples:
        if triple.predicate == "cityServed":
            city = triple.object
            airport = triple.subject
        elif triple.predicate == "country":
            country = triple.object
        elif triple.predicate == "capital":
            capital = triple.object
        elif triple.predicate == "location":
            location = triple.object
        elif triple.predicate == "elevationAboveTheSeaLevel":
            elevation = triple.object
        elif triple.predicate == "elevationAboveTheSeaLevelInFeet":
            elevation = triple.object
        elif triple.predicate == "elevationAboveTheSeaLevelInMetres":
            elevation = triple.object
        elif triple.predicate == "runwayLength":
            runway_length = triple.object
        elif triple.predicate == "1stRunwayLengthFeet":
            runway_length = triple.object

    if airport and city and country and capital:
        sentence = f"{airport} serves the city of {city} which is located in {location}, {country}, where the capital is {capital}"
    elif airport and city and country:
        sentence = f"{airport} serves the city of {city} which is located in {location}, {country}"
    elif airport and city:
        sentence = f"{airport} serves the city of {city} which is located in {location}"
    if elevation:
        sentence += f", {predicates['elevationAboveTheSeaLevel'].format(elevation)}"
    if runway_length:
        sentence += f", {predicates['runwayLength'].format(runway_length)}"
    for triple in triples:
        if triple.predicate not in ["cityServed", "country", "capital", "location", "elevationAboveTheSeaLevel", "elevationAboveTheSeaLevelInFeet", "elevationAboveTheSeaLevelInMetres", "runwayLength", "1stRunwayLengthFeet"]:
            sentence += f", {predicates[triple.predicate].format(triple.object)}"
    return sentence + "."

