def predict(triples: list[Triple]) -> str:
    subjects = {}
    for triple in triples:
        if triple.subject not in subjects:
            subjects[triple.subject] = []
        subjects[triple.subject].append((triple.predicate, triple.object))

    sentence = ""
    for subject, triples_list in subjects.items():
        sentence += f"{subject} "
        predicate_groups = {
            'location': [],
            'organisation': [],
            'identification': [],
            'airport_info': [],
            'other': []
        }

        for i, (predicate, obj) in enumerate(triples_list):
            if predicate in ['cityServed', 'location']:
                predicate_groups['location'].append((predicate, obj))
            elif predicate in ['operatingOrganisation', 'owner']:
                predicate_groups['organisation'].append((predicate, obj))
            elif predicate in ['iataLocationIdentifier', 'icaoLocationIdentifier']:
                predicate_groups['identification'].append((predicate, obj))
            elif predicate in ['elevationAboveTheSeaLevel', 'runwayLength', 'runwayName']:
                predicate_groups['airport_info'].append((predicate, obj))
            else:
                predicate_groups['other'].append((predicate, obj))

        for group_name, group in predicate_groups.items():
            if group:
                if group_name == 'location':
                    sentence += f"which is located in {group[0][1]} "
                elif group_name == 'organisation':
                    sentence += f"operated by {group[0][1]} "
                elif group_name == 'identification':
                    sentence += f"with the IATA code {group[0][1]} and the ICAO code {group[1][1]} "
                elif group_name == 'airport_info':
                    sentence += f"at an elevation of {group[0][1]} metres above sea level "
                    if len(group) > 1:
                        sentence += f"and has a runway length of {group[1][1]} metres "
                elif group_name == 'other':
                    sentence += f", {group[0][1]} "

        sentence += ". "

    # Remove the extra ". " at the end and add a period at the end of the sentence
    sentence = sentence.strip()[:-1] + "."

    return sentence
