from dataclasses import dataclass

@dataclass
class Triple:
    subject: str
    predicate: str
    object: str

# EVOLVE-BLOCK-START

def predict(triples: list[Triple]) -> str:
    # Create a graph from the triples
    graph = {}
    for triple in triples:
        if triple.subject not in graph:
            graph[triple.subject] = {}
        graph[triple.subject][triple.predicate] = triple.object

    sentences = []
    for subject, properties in graph.items():
        sentence = f"{subject}"
        if "cityServed" in properties:
            city = properties["cityServed"]
            sentence += f" serves the city of {city}"
            if "country" in properties:
                country = properties["country"]
                sentence += f" in {country}"
            elif "location" in properties:
                location = properties["location"]
                sentence += f" which is located in {location}"
        elif "location" in properties:
            location = properties["location"]
            sentence += f" is located in {location}"
        elif "country" in properties:
            country = properties["country"]
            sentence += f" in {country}"
        else:
            for predicate, obj in properties.items():
                sentence += f" has a {predicate} of {obj}, "
            sentence = sentence.rstrip(", ")
        sentences.append(sentence)

    # Create a complex sentence from all the given triples
    complex_sentence = ""
    airport = None
    city = None
    country = None
    capital = None
    for triple in triples:
        if triple.predicate == "cityServed":
            city = triple.object
            airport = triple.subject
        elif triple.predicate == "country":
            country = triple.object
        elif triple.predicate == "capital":
            capital = triple.object
    if airport and city and country and capital:
        complex_sentence = f"{airport} serves the city of {city} which is located in {country}, where the capital is {capital}"
    if complex_sentence:
        return complex_sentence + "."
    else:
        return ". ".join(sentences) + "."

