            airport_info.append((triple.subject, triple.predicate, triple.object))
        elif triple.predicate in ["country", "isPartOf", "language", "capital", "leader", "demonym"]:
            country_info.append((triple.subject, triple.predicate, triple.object))
        else:
            other_info.append((triple.subject, triple.predicate, triple.object))

    result_sentence = ""
