        airport_sentence = f"{airport_info[0][0]} "
        for i, (subject, predicate, obj) in enumerate(airport_info):
            if predicate in predicates:
                airport_sentence += predicates[predicate].format(obj)
                if i < len(airport_info) - 1:
                    airport_sentence += ", "
        result_sentence += airport_sentence + "."

    if country_info:
        if result_sentence:
            result_sentence += " "
        country_sentence = ""
        for i, (subject, predicate, obj) in enumerate(country_info):
            if predicate in predicates:
                country_sentence += predicates[predicate].format(obj)
                if i < len(country_info) - 1:
                    country_sentence += ", "
        result_sentence += country_sentence + "."

    if other_info:
        if result_sentence:
            result_sentence += " "
        other_sentence = ""
