from dataclasses import dataclass

@dataclass
class Triple:
    subject: str
    predicate: str
    object: str

# EVOLVE-BLOCK-START

def predict(triples: list[Triple]) -> str:
    subjects = {}
    for triple in triples:
        if triple.subject not in subjects:
            subjects[triple.subject] = []
        subjects[triple.subject].append((triple.predicate, triple.object))

    main_subject = None
    main_sentence = ""
    sub_sentences = []

    for subject, triples in subjects.items():
        if main_subject is None:
            main_subject = subject
            main_sentence += f"{subject}"
        predicate_sentence = ""
        for i, (predicate, obj) in enumerate(triples):
            if predicate == "cityServed":
                predicate_sentence += f" serves the city of {obj}"
            elif predicate == "location":
                predicate_sentence += f", located in {obj}"
            elif predicate == "elevationAboveTheSeaLevel":
                predicate_sentence += f", which is {obj} metres above sea level"
            elif predicate == "runwayLength":
                predicate_sentence += f", and has a runway length of {obj} metres"
            elif predicate == "country":
                predicate_sentence += f", in {obj}"
            elif predicate == "isPartOf":
                predicate_sentence += f", which is part of {obj}"
            elif predicate == "language":
                predicate_sentence += f", where the language spoken is {obj}"
            elif predicate == "capital":
                predicate_sentence += f", with {obj} as its capital"
            elif predicate == "nativeName":
                predicate_sentence += f", also known as {obj}"
            elif predicate == "operatingOrganisation":
                predicate_sentence += f", operated by {obj}"
            elif predicate == "iataLocationIdentifier":
                predicate_sentence += f", with the IATA code {obj}"
            elif predicate == "icaoLocationIdentifier":
                predicate_sentence += f", and the ICAO code {obj}"
            elif predicate == "country":
                predicate_sentence += f", which is in {obj}"
            elif predicate == "officialLanguage":
                predicate_sentence += f", where the official language is {obj}"
        if subject != main_subject:
            sub_sentences.append(f"{subject}{predicate_sentence}")
        else:
            main_sentence += predicate_sentence

    sentence = main_sentence
    if sub_sentences:
        sentence += ", which is related to " + ", and ".join(sub_sentences)
    sentence += "."
    return sentence.strip()

