from dataclasses import dataclass

@dataclass
class Triple:
    subject: str
    predicate: str
    object: str

# EVOLVE-BLOCK-START

def predict(triples: list[Triple]) -> str:
    airport_name = None
    city = None
    country = None
    other_info = []

    for triple in triples:
        if triple.predicate == "cityServed":
            city = triple.object
        elif triple.predicate == "country":
            country = triple.object
        elif triple.subject.startswith("Airport"):
            airport_name = triple.subject
        else:
            other_info.append(f"{triple.subject} {triple.predicate} {triple.object}")

    sentence = ""
    if airport_name and city and country:
        sentence = f"{airport_name} serves the city of {city} which is in {country}"
    elif airport_name and city:
        sentence = f"{airport_name} serves the city of {city}"
    elif airport_name:
        sentence = f"{airport_name} is an airport"

    if other_info:
        if sentence:
            sentence += ", where "
        else:
            sentence = "In "
        sentence += ", and ".join(other_info)

    return sentence + "."

