from dataclasses import dataclass

@dataclass
class Triple:
    subject: str
    predicate: str
    object: str

# EVOLVE-BLOCK-START

def predict(triples: list[Triple]) -> str:
    subject = triples[0].subject
    sentence = f"{subject} is an airport that "

    # Get all the predicates and objects for the subject
    predicates = {}
    for triple in triples:
        if triple.subject not in predicates:
            predicates[triple.subject] = []
        predicates[triple.subject].append((triple.predicate, triple.object))

    # Create a complex sentence with all the triples
    for i, (predicate, obj) in enumerate(predicates[subject]):
        if predicate == "cityServed":
            sentence += f"serves the city of {obj}"
        elif predicate == "elevationAboveTheSeaLevel":
            sentence += f"which is {obj} metres above sea level"
        elif predicate == "location":
            sentence += f"located in {obj}"
        elif predicate == "runwayLength":
            sentence += f"with a runway length of {obj} metres"
        else:
            sentence += f"where the {predicate} is {obj}"
        if i < len(predicates[subject]) - 1:
            sentence += ", "

    # Add additional information from other subjects
    for triple in triples:
        if triple.subject != subject:
            if triple.predicate == "country":
                sentence += f" and is located in the country of {triple.object}"
            elif triple.predicate == "isPartOf":
                sentence += f" and is part of {triple.object}"
            elif triple.predicate == "language":
                sentence += f" where the language spoken is {triple.object}"

    return sentence + "."

# EVOLVE-BLOCK-END
