from dataclasses import dataclass

@dataclass
class Triple:
    subject: str
    predicate: str
    object: str

# EVOLVE-BLOCK-START

def predict(triples: list[Triple]) -> str:
    subject = triples[0].subject
    sentence = f"{subject} is an airport "

    location = None
    city_served = None
    operating_organisation = None
    elevation = None
    runway_length = None
    runway_surface = None

    for triple in triples:
        if triple.predicate == "location":
            location = triple.object
        elif triple.predicate == "cityServed":
            city_served = triple.object
        elif triple.predicate == "operatingOrganisation":
            operating_organisation = triple.object
        elif triple.predicate == "elevationAboveTheSeaLevel":
            elevation = triple.object
        elif triple.predicate == "runwayLength":
            runway_length = triple.object
        elif triple.predicate == "1stRunwaySurfaceType":
            runway_surface = triple.object

    if location:
        sentence += f"located in {location}"
    if city_served:
        if location:
            sentence += f", which serves the city of {city_served}"
        else:
            sentence += f" that serves the city of {city_served}"
    if operating_organisation:
        sentence += f", operated by {operating_organisation}"
    if elevation:
        sentence += f", with an elevation of {elevation} meters above sea level"
    if runway_length:
        sentence += f", and has a runway length of {runway_length} meters"
    if runway_surface:
        sentence += f", with its 1st runway surface made of {runway_surface}"

    for triple in triples:
        if triple.predicate not in ["location", "cityServed", "operatingOrganisation", "elevationAboveTheSeaLevel", "runwayLength", "1stRunwaySurfaceType"]:
            sentence += f", and {triple.subject} {triple.predicate} {triple.object}"

    return sentence + "."

