from dataclasses import dataclass

@dataclass
class Triple:
    subject: str
    predicate: str
    object: str

# EVOLVE-BLOCK-START

def predict(triples: list[Triple]) -> str:
    subjects = {}
    for triple in triples:
        if triple.subject not in subjects:
            subjects[triple.subject] = []
        subjects[triple.subject].append((triple.predicate, triple.object))

    sentence = ""
    city_served = ""
    location = ""
    country = ""
    elevation = ""
    runway_length = ""
    operating_organisation = ""
    icao_code = ""
    iata_code = ""
    native_name = ""
    for triple in triples:
        if triple.predicate == "cityServed":
            city_served = f"serves the city of {triple.object}"
        elif triple.predicate == "location":
            location = f"located in {triple.object}"
        elif triple.predicate == "elevationAboveTheSeaLevel":
            elevation = f"which is {triple.object} metres above sea level"
        elif triple.predicate == "runwayLength":
            runway_length = f"with a runway length of {triple.object} metres"
        elif triple.predicate == "country":
            country = f"in {triple.object}"
        elif triple.predicate == "operatingOrganisation":
            operating_organisation = f"operated by {triple.object}"
        elif triple.predicate == "icaoLocationIdentifier":
            icao_code = f"and the ICAO code {triple.object}"
        elif triple.predicate == "iataLocationIdentifier":
            iata_code = f"with the IATA code {triple.object}"
        elif triple.predicate == "nativeName":
            native_name = f"also known as {triple.object}"

    sentence += f"{city_served} "
    if location:
        sentence += f"{location} "
    if country:
        sentence += f"{country} "
    if elevation:
        sentence += f"{elevation} "
    if runway_length:
        sentence += f"{runway_length} "
    if operating_organisation:
        sentence += f"{operating_organisation} "
    if icao_code:
        sentence += f"{icao_code} "
    if iata_code:
        sentence += f"{iata_code} "
    if native_name:
        sentence += f"{native_name} "

    sentence = sentence.strip() + "."

    return sentence

