def predict(triples: list[Triple]) -> str:
    subjects = {}
    for triple in triples:
        if triple.subject not in subjects:
            subjects[triple.subject] = []
        subjects[triple.subject].append((triple.predicate, triple.object))

    sentence = ""
    for subject, triples_list in subjects.items():
        sentence += f"{subject} "
        city_served = ""
        location = ""
        country = ""
        elevation = ""
        runway_length = ""
        operating_organisation = ""
        for i, (predicate, obj) in enumerate(triples_list):
            if predicate == "cityServed":
                city_served = f"serves the city of {obj}"
            elif predicate == "location":
                location = f"located in {obj}"
            elif predicate == "elevationAboveTheSeaLevel":
                elevation = f"which is {obj} metres above sea level"
            elif predicate == "runwayLength":
                runway_length = f"with a runway length of {obj} metres"
            elif predicate == "country":
                country = f"in {obj}"
            elif predicate == "operatingOrganisation":
                operating_organisation = f"operated by {obj}"

        sentence += f"{city_served} "
        if location:
            sentence += f"in {location} "
        if country:
            sentence += f"{country} "
        if elevation:
            sentence += f"{elevation} "
        if runway_length:
            sentence += f"{runway_length} "
        if operating_organisation:
            sentence += f"{operating_organisation} "

        sentence += ". "

    # Remove the extra ". " at the end and add a period at the end of the sentence
    sentence = sentence.strip()[:-1] + "."

    return sentence
