from dataclasses import dataclass

@dataclass
class Triple:
    subject: str
    predicate: str
    object: str

# EVOLVE-BLOCK-START

def predict(triples: list[Triple]) -> str:
    airport_name = None
    city_served = None
    country = None
    elevation = None
    runway_length = None
    location = None
    iata_code = None
    icao_code = None
    operating_organisation = None

    for triple in triples:
        if triple.predicate == "cityServed":
            city_served = triple.object
        elif triple.predicate == "country":
            country = triple.object
        elif triple.predicate == "elevationAboveTheSeaLevel":
            elevation = triple.object
        elif triple.predicate == "runwayLength":
            runway_length = triple.object
        elif triple.predicate == "location":
            location = triple.object
        elif triple.predicate == "iataLocationIdentifier":
            iata_code = triple.object
        elif triple.predicate == "icaoLocationIdentifier":
            icao_code = triple.object
        elif triple.predicate == "operatingOrganisation":
            operating_organisation = triple.object
        elif triple.subject not in [city_served, country, elevation, runway_length, location, iata_code, icao_code, operating_organisation]:
            airport_name = triple.subject

    sentence = ""
    if airport_name:
        sentence += f"{airport_name} "
    if city_served:
        sentence += f"is located in {city_served} "
    if country:
        sentence += f"which is in {country} "
    if elevation:
        sentence += f"at an elevation of {elevation} metres above sea level "
    if runway_length:
        sentence += f"and has a runway length of {runway_length} metres "
    if location:
        sentence += f"which is located in {location} "
    if iata_code:
        sentence += f"with the IATA code {iata_code} "
    if icao_code:
        sentence += f"and the ICAO code {icao_code} "
    if operating_organisation:
        sentence += f"operated by {operating_organisation} "

    # Remove the extra " " at the end and add a period at the end of the sentence
    sentence = sentence.strip() + "."

    return sentence

