from dataclasses import dataclass

@dataclass
class Triple:
    subject: str
    predicate: str
    object: str

# EVOLVE-BLOCK-START

def predict(triples: list[Triple]) -> str:
    airport_info = {}
    location_info = {}
    country_info = {}

    for triple in triples:
        if triple.predicate == "cityServed":
            airport_info[triple.subject] = {"city": triple.object}
        elif triple.predicate == "location":
            airport_info[triple.subject] = {"location": triple.object}
        elif triple.predicate == "elevationAboveTheSeaLevel":
            airport_info[triple.subject] = {"elevation": triple.object}
        elif triple.predicate == "runwayLength":
            airport_info[triple.subject] = {"runway_length": triple.object}
        elif triple.predicate == "country":
            location_info[triple.object] = {"country": triple.object}
            country_info[triple.object] = {}
        elif triple.predicate == "isPartOf":
            location_info[triple.object] = {"part_of": triple.object}
        elif triple.predicate == "language":
            country_info[triple.object] = {"language": triple.object}
        elif triple.predicate == "capital":
            country_info[triple.object] = {"capital": triple.object}

    sentence = ""
    for airport, info in airport_info.items():
        sentence += f"{airport} "
        if "city" in info:
            sentence += f"serves the city of {info['city']} "
        if "location" in info:
            sentence += f"which is located in {info['location']} "
        if "elevation" in info:
            sentence += f"at an elevation of {info['elevation']} metres above sea level "
        if "runway_length" in info:
            sentence += f"and has a runway length of {info['runway_length']} metres "
        if "location" in info:
            location = info["location"]
            if location in location_info:
                if "part_of" in location_info[location]:
                    sentence += f"which is part of {location_info[location]['part_of']} "
                if location in country_info:
                    if "language" in country_info[location]:
                        sentence += f"where the primary language spoken is {country_info[location]['language']} "
                    if "capital" in country_info[location]:
                        sentence += f"where the capital city is {country_info[location]['capital']} "
        sentence += ". "

    # Remove the extra ". " at the end and add a period at the end of the sentence
    sentence = sentence.strip()[:-1] + "."

    return sentence

