        if subject not in airport_info:
            airport_info[subject] = {"cityServed": None, "location": None, "country": None}

        if predicate == "cityServed":
            airport_info[subject]["cityServed"] = obj
        elif predicate == "location":
            airport_info[subject]["location"] = obj
        elif predicate == "country":
