from dataclasses import dataclass

@dataclass
class Triple:
    subject: str
    predicate: str
    object: str

# EVOLVE-BLOCK-START

def predict(triples: list[Triple]) -> str:
    subject_dict = {}
    for triple in triples:
        if triple.subject not in subject_dict:
            subject_dict[triple.subject] = []
        subject_dict[triple.subject].append((triple.predicate, triple.object))

    sentences = []
    for subject, triples_list in subject_dict.items():
        sentence = f"{subject} "
        for predicate, obj in triples_list:
            if predicate == "cityServed":
                sentence += f"serves the city of {obj}, "
            elif predicate == "location":
                sentence += f"located in {obj}, "
            elif predicate == "elevationAboveTheSeaLevel":
                sentence += f"with an elevation of {obj} meters above sea level, "
            elif predicate == "country":
                sentence += f"located in {obj}, "
            elif predicate == "isPartOf":
                sentence += f"part of {obj}, "
            elif predicate == "language":
                sentence += f"where the language spoken is {obj}, "
            elif predicate == "owner":
                sentence += f"owned by {obj}, "
            elif predicate == "operatingOrganisation":
                sentence += f"operated by {obj}, "
            elif predicate == "runwayLength":
                sentence += f"with a runway length of {obj} meters, "
            elif predicate == "icaoLocationIdentifier":
                sentence += f"with the ICAO location identifier {obj}, "
            else:
                sentence += f"has {predicate} {obj}, "

        sentence = sentence.rstrip(", ") + "."
        sentences.append(sentence)

    # Try to create one complex sentence from all the given triples
    complex_sentence = ""
    for subject, triples_list in subject_dict.items():
        for predicate, obj in triples_list:
            if complex_sentence == "":
                complex_sentence += f"{subject} {get_verb(predicate)} {obj}"
            else:
                complex_sentence += f", which {get_verb(predicate)} {obj}"

    def get_verb(predicate):
        verb_dict = {
            "cityServed": "serves",
            "location": "is located in",
            "elevationAboveTheSeaLevel": "has an elevation of",
            "country": "is located in",
            "isPartOf": "is part of",
            "language": "speaks",
            "owner": "is owned by",
            "operatingOrganisation": "is operated by",
            "runwayLength": "has a runway length of",
            "icaoLocationIdentifier": "has the ICAO location identifier"
        }
        return verb_dict.get(predicate, "has")

    if complex_sentence != "":
        return complex_sentence + "."
    else:
        return " ".join(sentences)

