def predict(triples: list[Triple]) -> str:
    subjects = {}
    predicates = {
        "cityServed": "serves the city of {}",
        "location": "is located in {}",
        "elevationAboveTheSeaLevel": "which is {} metres above sea level",
        "runwayLength": "and has a runway length of {} metres",
        "country": "in {}",
        "isPartOf": "which is part of {}",
        "language": "where the language spoken is {}",
        "capital": "where the capital is {}",
        "operatingOrganisation": "operated by {}",
        "nativeName": "also known as {}",
        "iataLocationIdentifier": "with the IATA code {}",
        "icaoLocationIdentifier": "and the ICAO code {}",
        "1stRunwaySurfaceType": "and has a 1st runway surface type of {}",
        "1stRunwayLengthFeet": "and has a 1st runway length of {} feet",
        "leader": "led by {}",
        "demonym": "with the demonym of {}",
        "order": "belonging to the order of {}",
        "class": "and belonging to the class of {}",
    }

    for triple in triples:
        if triple.subject not in subjects:
            subjects[triple.subject] = []
        subjects[triple.subject].append((triple.predicate, triple.object))

    sentences = []
    for subject, triples_list in subjects.items():
        sentence = f"{subject} "
        for i, (predicate, obj) in enumerate(triples_list):
            if predicate in predicates:
                sentence += predicates[predicate].format(obj)
                if i < len(triples_list) - 1:
                    sentence += ", "
        sentences.append(sentence + ".")

    # Try to generate a single complex sentence
    if len(sentences) > 1:
        complex_sentence = sentences[0].strip()[:-1] + " which "
        for sentence in sentences[1:]:
            complex_sentence += sentence.strip()[:-1] + ", "
        complex_sentence = complex_sentence.strip()[:-1] + "."
        return complex_sentence
    else:
        # Try to create a single complex sentence from the triples
        complex_sentence = ""
        for triple in triples:
            if triple.predicate == "cityServed":
                complex_sentence += f"{triple.subject} serves the city of {triple.object} "
            elif triple.predicate == "location":
                complex_sentence += f"which is located in {triple.object} "
            elif triple.predicate == "country":
                complex_sentence += f"within the country of {triple.object} "
            elif triple.predicate == "capital":
                complex_sentence += f"where the capital city is {triple.object} "
            elif triple.predicate == "nativeName":
                complex_sentence += f"also known as {triple.object} "
            elif triple.predicate == "iataLocationIdentifier":
                complex_sentence += f"with the IATA code {triple.object} "
            elif triple.predicate == "icaoLocationIdentifier":
                complex_sentence += f"and the ICAO code {triple.object} "
            elif triple.predicate == "operatingOrganisation":
                complex_sentence += f"operated by {triple.object} "
            elif triple.predicate == "1stRunwaySurfaceType":
                complex_sentence += f"and has a 1st runway surface type of {triple.object} "
            elif triple.predicate == "1stRunwayLengthFeet":
                complex_sentence += f"and has a 1st runway length of {triple.object} feet "
            elif triple.predicate == "class":
                complex_sentence += f"which belongs to the class {triple.object} "
            elif triple.predicate == "order":
                complex_sentence += f"and belongs to the order of {triple.object} "
        return complex_sentence.strip() + "."

