from dataclasses import dataclass

@dataclass
class Triple:
    subject: str
    predicate: str
    object: str

# EVOLVE-BLOCK-START

def predict(triples: list[Triple]) -> str:
    subjects = {}
    for triple in triples:
        if triple.subject not in subjects:
            subjects[triple.subject] = []
        subjects[triple.subject].append((triple.predicate, triple.object))

    sentence = ""
    subjects_list = list(subjects.keys())
    for i, subject in enumerate(subjects_list):
        triples_list = subjects[subject]
        related_triples = []
        for predicate, obj in triples_list:
            if predicate == "cityServed":
                related_triples.append(f"serves the city of {obj}")
            elif predicate == "location":
                related_triples.append(f"is located in {obj}")
            elif predicate == "elevationAboveTheSeaLevel":
                related_triples.append(f"which is {obj} metres above sea level")
            elif predicate == "runwayLength":
                related_triples.append(f"and has a runway length of {obj} metres")
            elif predicate == "country":
                related_triples.append(f"in {obj}")
            elif predicate == "isPartOf":
                related_triples.append(f"which is part of {obj}")
            elif predicate == "language":
                related_triples.append(f"where the language spoken is {obj}")
            elif predicate == "capital":
                related_triples.append(f"where the capital is {obj}")
            elif predicate == "nativeName":
                related_triples.append(f"also known as {obj}")
            elif predicate == "iataLocationIdentifier":
                related_triples.append(f"with the IATA code {obj}")
            elif predicate == "icaoLocationIdentifier":
                related_triples.append(f"and the ICAO code {obj}")
            elif predicate == "operatingOrganisation":
                related_triples.append(f"operated by {obj}")
            elif predicate == "leader":
                related_triples.append(f"led by {obj}")
            elif predicate == "owner":
                related_triples.append(f"owned by {obj}")
            # Additional predicates
            elif predicate == "1stRunwaySurfaceType":
                related_triples.append(f"with the first runway surface type of {obj}")
            elif predicate == "3rdRunwaySurfaceType":
                related_triples.append(f"with the third runway surface type of {obj}")
            elif predicate == "4thRunwaySurfaceType":
                related_triples.append(f"with the fourth runway surface type of {obj}")
            elif predicate == "5thRunwaySurfaceType":
                related_triples.append(f"with the fifth runway surface type of {obj}")
            elif predicate == "administrativeArrondissement":
                related_triples.append(f"in the administrative arrondissement of {obj}")
            elif predicate == "areaCode":
                related_triples.append(f"with the area code of {obj}")
            elif predicate == "ceremonialCounty":
                related_triples.append(f"in the ceremonial county of {obj}")
            elif predicate == "chief":
                related_triples.append(f"with the chief of {obj}")
            elif predicate == "countySeat":
                related_triples.append(f"with the county seat of {obj}")
            elif predicate == "currency":
                related_triples.append(f"with the currency of {obj}")
            elif predicate == "demonym":
                related_triples.append(f"with the demonym of {obj}")
            elif predicate == "foundingYear":
                related_triples.append(f"founded in the year {obj}")
            elif predicate == "headquarter":
                related_triples.append(f"with the headquarter of {obj}")
            elif predicate == "hubAirport":
                related_triples.append(f"with the hub airport of {obj}")
            elif predicate == "jurisdiction":
                related_triples.append(f"with the jurisdiction of {obj}")
            elif predicate == "largestCity":
                related_triples.append(f"with the largest city of {obj}")
            elif predicate == "leaderParty":
                related_triples.append(f"with the leader party of {obj}")
            elif predicate == "leaderTitle":
                related_triples.append(f"with the leader title of {obj}")
            elif predicate == "locationIdentifier":
                related_triples.append(f"with the location identifier of {obj}")
            elif predicate == "mayor":
                related_triples.append(f"with the mayor of {obj}")
            elif predicate == "officialLanguage":
                related_triples.append(f"with the official language of {obj}")
            elif predicate == "postalCode":
                related_triples.append(f"with the postal code of {obj}")
            elif predicate == "regionServed":
                related_triples.append(f"with the region served of {obj}")
            elif predicate == "transportAircraft":
                related_triples.append(f"with the transport aircraft of {obj}")
        if i == 0:
            sentence += f"{subject} "
            if len(related_triples) > 1:
                sentence += ", which ".join(related_triples)
            elif len(related_triples) == 1:
                sentence += related_triples[0]
        else:
            sentence += f", {subject} "
            if len(related_triples) > 1:
                sentence += ", which ".join(related_triples)
            elif len(related_triples) == 1:
                sentence += related_triples[0]

    # Remove the extra ", " at the end and add a period at the end of the sentence
    sentence = sentence.strip() + "."

    # Check for related triples across different subjects
    for triple in triples:
        if triple.predicate == "operatingOrganisation":
            organisation = triple.object
            for other_triple in triples:
                if other_triple.subject == organisation:
                    sentence += f" The organisation {organisation} has a {other_triple.predicate} of {other_triple.object}."
        elif triple.predicate == "leader":
            leader = triple.object
            for other_triple in triples:
                if other_triple.subject == leader:
                    sentence += f" The leader {leader} has a {other_triple.predicate} of {other_triple.object}."

    return sentence

