from dataclasses import dataclass

@dataclass
class Triple:
    subject: str
    predicate: str
    object: str

# EVOLVE-BLOCK-START

def predict(triples: list[Triple]) -> str:
    subject = triples[0].subject
    sentence = f"{subject} is an airport that "
    for triple in triples:
        if triple.predicate == "cityServed":
            sentence += f"serves the city of {triple.object} "
        elif triple.predicate == "location":
            sentence += f"located in {triple.object} "
        elif triple.predicate == "operatingOrganisation":
            sentence += f"operated by {triple.object} "
        elif triple.predicate == "elevationAboveTheSeaLevel":
            sentence += f"with an elevation of {triple.object} meters above sea level "
        elif triple.predicate == "runwayLength":
            sentence += f"has a runway length of {triple.object} meters "
        elif triple.predicate == "1stRunwaySurfaceType":
            sentence += f"with its 1st runway surface made of {triple.object} "
        elif triple.predicate == "3rdRunwaySurfaceType":
            sentence += f"and its 3rd runway surface made of {triple.object} "
        else:
            sentence += f"has a {triple.predicate} of {triple.object} "
    return sentence.strip() + "."

