from dataclasses import dataclass

@dataclass
class Triple:
    subject: str
    predicate: str
    object: str

# EVOLVE-BLOCK-START

def predict(triples: list[Triple]) -> str:
    airport_info = {}
    country_info = {}
    other_info = {}

    predicates = {
        "cityServed": "serves the city of {}",
        "location": "is located in {}",
        "elevationAboveTheSeaLevel": "which is {} metres above sea level",
        "runwayLength": "and has a runway length of {} metres",
        "country": "in {}",
        "isPartOf": "which is part of {}",
        "language": "where the language spoken is {}",
        "capital": "where the capital is {}",
        "operatingOrganisation": "operated by {}",
        "nativeName": "also known as {}",
        "iataLocationIdentifier": "with the IATA code {}",
        "icaoLocationIdentifier": "and the ICAO code {}",
        "1stRunwaySurfaceType": "and has a 1st runway surface type of {}",
        "1stRunwayLengthFeet": "and has a 1st runway length of {} feet",
        "leader": "led by {}",
        "demonym": "with the demonym of {}",
        "order": "belonging to the order of {}",
        "class": "and belonging to the class of {}",
        "runwayName": "has a runway named {}",
        "1stRunwayLengthMetre": "and its first runway is {} metres long",
    }

    for triple in triples:
        if triple.predicate in ["cityServed", "location", "elevationAboveTheSeaLevel", "runwayLength", "operatingOrganisation", "nativeName", "iataLocationIdentifier", "icaoLocationIdentifier", "1stRunwaySurfaceType", "1stRunwayLengthFeet", "1stRunwayLengthMetre", "runwayName"]:
            if triple.subject not in airport_info:
                airport_info[triple.subject] = []
            airport_info[triple.subject].append((triple.predicate, triple.object))
        elif triple.predicate in ["country", "isPartOf", "language", "capital", "leader", "demonym"]:
            if triple.subject not in country_info:
                country_info[triple.subject] = []
            country_info[triple.subject].append((triple.predicate, triple.object))
        else:
            if triple.subject not in other_info:
                other_info[triple.subject] = []
            other_info[triple.subject].append((triple.predicate, triple.object))

    result_sentence = ""
    # Try to generate a single complex sentence
    subjects = list(airport_info.keys()) + list(country_info.keys()) + list(other_info.keys())
    complex_sentence = ""
    for subject in subjects:
        if subject in airport_info:
            triples_list = airport_info[subject]
        elif subject in country_info:
            triples_list = country_info[subject]
        else:
            triples_list = other_info[subject]

        sentence = f"{subject} "
        for i, (predicate, obj) in enumerate(triples_list):
            if predicate in predicates:
                sentence += predicates[predicate].format(obj)
                if i < len(triples_list) - 1:
                    sentence += ", "
        if complex_sentence:
            complex_sentence += " which is " + sentence.strip()[:-1] + ", "
        else:
            complex_sentence += sentence.strip()[:-1] + ", "
    result_sentence = complex_sentence.strip()[:-1] + "."

    # Remove the extra ". " at the end and add a period at the end of the sentence
    result_sentence = result_sentence.strip()[:-1] + "."

    return result_sentence

# EVOLVE-BLOCK-END
