from dataclasses import dataclass

@dataclass
class Triple:
    subject: str
    predicate: str
    object: str

# EVOLVE-BLOCK-START

def predict(triples: list[Triple]) -> str:
    airport_info = {}
    location_info = {}
    country_info = {}
    subjects = {}

    for triple in triples:
        subject = triple.subject
        predicate = triple.predicate
        obj = triple.object

        if subject not in subjects:
            subjects[subject] = []
        subjects[subject].append((predicate, obj))

    sentence = ""
    for subject, triples_list in subjects.items():
        sentence += f"{subject} "
        main_description = ""
        additional_descriptions = []
        for i, (predicate, obj) in enumerate(triples_list):
            if predicate == "cityServed":
                main_description += f"serves the city of {obj} "
            elif predicate == "location":
                main_description += f"located in {obj} "
            elif predicate == "elevationAboveTheSeaLevel":
                main_description += f"at an elevation of {obj} metres above sea level "
            elif predicate == "runwayLength":
                main_description += f"with a runway length of {obj} metres "
            elif predicate == "country":
                main_description += f"in {obj} "
            elif predicate == "isPartOf":
                additional_descriptions.append(f"part of {obj}")
            elif predicate == "language":
                additional_descriptions.append(f"where the language spoken is {obj}")
            elif predicate == "capital":
                additional_descriptions.append(f"with the capital being {obj}")
            elif predicate == "nativeName":
                additional_descriptions.append(f"also known as {obj}")
            elif predicate == "iataLocationIdentifier":
                additional_descriptions.append(f"with the IATA code {obj}")
            elif predicate == "icaoLocationIdentifier":
                additional_descriptions.append(f"and the ICAO code {obj}")
            elif predicate == "1stRunwaySurfaceType":
                additional_descriptions.append(f"with a 1st runway surface type of {obj}")
            elif predicate == "1stRunwayLengthFeet":
                additional_descriptions.append(f"and a 1st runway length of {obj} feet")
            elif predicate == "operatingOrganisation":
                additional_descriptions.append(f"operated by {obj}")
            elif predicate == "aircraftHelicopter":
                additional_descriptions.append(f"with an aircraft helicopter of {obj}")

        if main_description:
            sentence += main_description
        if additional_descriptions:
            sentence += "which is " + ", ".join(additional_descriptions)
        sentence += ". "

    # Remove the extra ". " at the end and add a period at the end of the sentence
    sentence = sentence.strip()[:-1] + "."

    return sentence

