from dataclasses import dataclass

@dataclass
class Triple:
    subject: str
    predicate: str
    object: str

# EVOLVE-BLOCK-START

def predict(triples: list[Triple]) -> str:
    # Create a graph from the triples
    graph = {}
    for triple in triples:
        if triple.subject not in graph:
            graph[triple.subject] = {}
        graph[triple.subject][triple.predicate] = triple.object

    sentences = []
    for subject, properties in graph.items():
        sentence = f"{subject} "
        predicates = {}
        for predicate, obj in properties.items():
            if predicate not in predicates:
                predicates[predicate] = []
            predicates[predicate].append(obj)

        for predicate, objects in predicates.items():
            if predicate == "cityServed":
                sentence += f"serves the city of {', '.join(objects)}, "
            elif predicate == "location":
                sentence += f"located in {', '.join(objects)}, "
            elif predicate == "elevationAboveTheSeaLevel":
                sentence += f"with an elevation of {', '.join(objects)} meters above sea level, "
            elif predicate == "country":
                sentence += f"located in {', '.join(objects)}, "
            elif predicate == "isPartOf":
                sentence += f"part of {', '.join(objects)}, "
            elif predicate == "language":
                sentence += f"where the language spoken is {', '.join(objects)}, "
            elif predicate == "owner":
                sentence += f"owned by {', '.join(objects)}, "
            elif predicate == "operatingOrganisation":
                sentence += f"operated by {', '.join(objects)}, "
            elif predicate == "runwayLength":
                sentence += f"with a runway length of {', '.join(objects)} meters, "
            elif predicate == "icaoLocationIdentifier":
                sentence += f"with the ICAO location identifier {', '.join(objects)}, "
            elif predicate == "nativeName":
                sentence += f"also known as {', '.join(objects)}, "
            elif predicate == "iataLocationIdentifier":
                sentence += f"with the IATA location identifier {', '.join(objects)}, "

        sentence = sentence.rstrip(", ") + "."
        sentences.append(sentence)

    # Create a complex sentence from all the given triples
    complex_sentence = ""
    airport = None
    city = None
    country = None
    capital = None
    location = None
    for triple in triples:
        if triple.predicate == "cityServed":
            city = triple.object
            airport = triple.subject
        elif triple.predicate == "country":
            country = triple.object
        elif triple.predicate == "capital":
            capital = triple.object
        elif triple.predicate == "location":
            location = triple.object
    if airport and city and country and capital:
        complex_sentence = f"{airport} serves the city of {city} which is located in {location}, {country}, where the capital is {capital}"
    elif airport and city and country:
        complex_sentence = f"{airport} serves the city of {city} which is located in {location}, {country}"
    elif airport and city:
        complex_sentence = f"{airport} serves the city of {city} which is located in {location}"
    if complex_sentence:
        return complex_sentence + "."
    else:
        return ". ".join(sentences) + "."

