            airport_info.append(f"which is {triple.object} metres above sea level")
        elif triple.predicate == "runwayLength":
            airport_info.append(f"and has a runway length of {triple.object} metres")
        elif triple.predicate == "country":
            country_info.append(f"in {triple.object}")
        elif triple.predicate == "isPartOf":
            location_info.append(f"which is part of {triple.object}")
        elif triple.predicate == "language":
            location_info.append(f"where the language spoken is {triple.object}")
        elif triple.predicate == "capital":
            country_info.append(f"where the capital is {triple.object}")

