def predict(triples: list[Triple]) -> str:
    airport_info = []
    location_info = []
    country_info = []
    language_info = []
    other_info = []

    for triple in triples:
        if triple.predicate == "cityServed":
            airport_info.append(f"serves the city of {triple.object}")
        elif triple.predicate == "location":
            location_info.append(f"located in {triple.object}")
        elif triple.predicate == "country":
            country_info.append(f"in {triple.object}")
        elif triple.predicate == "language":
            language_info.append(f"where the language spoken is {triple.object}")
        elif triple.predicate == "elevationAboveTheSeaLevel":
            airport_info.append(f"with an elevation of {triple.object} meters above sea level")
        elif triple.predicate == "1stRunwaySurfaceType":
            airport_info.append(f"has a 1st runway made of {triple.object}")
        elif triple.predicate == "runwayLength":
            airport_info.append(f"has a runway length of {triple.object} meters")
        else:
            other_info.append(f"{triple.subject} {triple.predicate} {triple.object}")

    sentence = ""
    if airport_info:
        sentence += f"{triples[0].subject} is an airport that "
        sentence += ", ".join(airport_info)
        sentence += ". "
    if location_info:
        sentence += "It is "
        sentence += ", ".join(location_info)
        sentence += ". "
    if country_info:
        sentence += "The country is "
        sentence += ", ".join(country_info)
        sentence += ". "
    if language_info:
        sentence += "The language spoken is "
        sentence += ", ".join(language_info)
        sentence += ". "
    if other_info:
        sentence += "Additionally, "
        sentence += ", ".join(other_info)
        sentence += "."

    return sentence.strip()

