                sentence += f"and has a runway length of {', '.join(objects)} meters, "
            elif predicate == "icaoLocationIdentifier":
                sentence += f"with the ICAO location identifier {', '.join(objects)}, "
            elif predicate == "capital":
                sentence += f"where the capital is {', '.join(objects)}, "
            elif predicate == "demonym":
                sentence += f"and the inhabitants are known as {', '.join(objects)}, "

        sentence = sentence.rstrip(", ") + "."
        sentences.append(sentence)

