    if len(sentences) > 1:
        combined_sentence = sentences[0]
        for sentence in sentences[1:]:
            subject = sentence.split(" ")[0]
            rest = sentence.split(" ", 1)[1]
            combined_sentence += f" which is {rest}"
        return combined_sentence
    else:
        return " ".join(sentences)

