def predict(triples: list[Triple]) -> str:
    subjects = {}
    for triple in triples:
        if triple.subject not in subjects:
            subjects[triple.subject] = []
        subjects[triple.subject].append((triple.predicate, triple.object))

    complex_sentence = ""
    for subject, predicates in subjects.items():
        subject_descriptions = []
        for predicate, obj in predicates:
            if predicate == "associatedBand/associatedMusicalArtist":
                subject_descriptions.append(f"is associated with the band {obj}")
            elif predicate == "genre":
                subject_descriptions.append(f"performs in the {obj} genre")
            elif predicate == "instrument":
                subject_descriptions.append(f"plays the {obj}")
            elif predicate == "occupation":
                subject_descriptions.append(f"works as a {obj}")
            elif predicate == "background":
                subject_descriptions.append(f"has a background as a {obj}")
            elif predicate == "birthPlace":
                subject_descriptions.append(f"was born in {obj}")
            elif predicate == "birthDate":
                subject_descriptions.append(f"was born on {obj}")
            elif predicate == "activeYearsStartYear":
                subject_descriptions.append(f"started their career in {obj}")
            elif predicate == "activeYearsEndYear":
                subject_descriptions.append(f"ended their career in {obj}")
            elif predicate == "recordLabel":
                subject_descriptions.append(f"is signed to {obj} records")
            elif predicate == "origin":
                subject_descriptions.append(f"is from {obj}")
            elif predicate == "language":
                subject_descriptions.append(f"speaks {obj}")
            else:
                subject_descriptions.append(f"has a {predicate} of {obj}")

        if subject_descriptions:
            subject_sentence = f"{subject} who {', '.join(subject_descriptions)}"
            if complex_sentence == "":
                complex_sentence += subject_sentence
            else:
                complex_sentence += f", and {subject_sentence}"

        # Check for related subjects
        for triple in triples:
            if triple.subject != subject and triple.object == subject:
