from dataclasses import dataclass

@dataclass
class Triple:
    subject: str
    predicate: str
    object: str

# EVOLVE-BLOCK-START

def predict(triples: list[Triple]) -> str:
    subjects = {}
    for triple in triples:
        if triple.subject not in subjects:
            subjects[triple.subject] = []
        subjects[triple.subject].append((triple.predicate, triple.object))

    sentences = []
    for subject, predicates in subjects.items():
        sentence = f"{subject} "
        associated_bands = []
        genres = []
        instruments = []
        occupations = []
        backgrounds = []
        birth_places = []
        other_predicates = []
        birth_years = []
        active_years_start = []
        active_years_end = []
        record_labels = []

        for predicate, obj in predicates:
            if predicate == "associatedBand/associatedMusicalArtist":
                associated_bands.append(obj)
            elif predicate == "genre":
                genres.append(obj)
            elif predicate == "instrument":
                instruments.append(obj)
            elif predicate == "occupation":
                occupations.append(obj)
            elif predicate == "background":
                backgrounds.append(obj)
            elif predicate == "birthPlace":
                birth_places.append(obj)
            elif predicate == "birthYear":
                birth_years.append(obj)
            elif predicate == "activeYearsStartYear":
                active_years_start.append(obj)
            elif predicate == "activeYearsEndYear":
                active_years_end.append(obj)
            elif predicate == "recordLabel":
                record_labels.append(obj)
            else:
                other_predicates.append((predicate, obj))

        if genres:
            sentence += f"is a {', '.join(genres)} musician"
        if instruments:
            sentence += f" who plays the {', '.join(instruments)}"
        if occupations:
            sentence += f" and works as a {', '.join(occupations)}"
        if backgrounds:
            sentence += f" with a background as {', '.join(backgrounds)}"
        if birth_places:
            sentence += f", born in {', '.join(birth_places)}"
        if birth_years:
            sentence += f", born in {', '.join(birth_years)}"
        if active_years_start:
            sentence += f", active since {', '.join(active_years_start)}"
        if active_years_end:
            sentence += f", active until {', '.join(active_years_end)}"
        if associated_bands:
            sentence += f", and is associated with the bands {', '.join(associated_bands)}"
        if record_labels:
            sentence += f", and is signed to {', '.join(record_labels)} records"
        for predicate, obj in other_predicates:
            sentence += f", with a {predicate} of {obj}"

        sentences.append(sentence)

    # Try to create a complex sentence from all the given triples
    if len(sentences) > 1:
        complex_sentence = sentences[0].replace('.', '')
        subjects = [sentence.split(' ')[0] for sentence in sentences]
        associations = {}
        for triple in triples:
            if triple.predicate in ["associatedBand/associatedMusicalArtist", "genre", "instrument", "occupation", "background", "birthPlace", "leader", "country"]:
                if triple.subject not in associations:
                    associations[triple.subject] = []
                associations[triple.subject].append((triple.predicate, triple.object))

        for sentence in sentences[1:]:
            subject = sentence.split(' ')[0]
            rest_of_sentence = ' '.join(sentence.split(' ')[1:]).replace('.', '')
            if subject in associations:
                associated_predicates = associations[subject]
                associated_descriptions = []
                for predicate, obj in associated_predicates:
                    if predicate == "associatedBand/associatedMusicalArtist":
                        associated_descriptions.append(f"associated with {obj}")
                    elif predicate == "genre":
                        associated_descriptions.append(f"in the {obj} genre")
                    elif predicate == "instrument":
                        associated_descriptions.append(f"playing the {obj}")
                    elif predicate == "occupation":
                        associated_descriptions.append(f"working as a {obj}")
                    elif predicate == "background":
                        associated_descriptions.append(f"with a background as a {obj}")
                    elif predicate == "birthPlace":
                        associated_descriptions.append(f"born in {obj}")
                    elif predicate == "leader":
                        associated_descriptions.append(f"led by {obj}")
                    elif predicate == "country":
                        associated_descriptions.append(f"located in {obj}")
                if associated_descriptions:
                    complex_sentence += f", who is {', '.join(associated_descriptions)} and {rest_of_sentence}"
                else:
                    complex_sentence += f", and {subject} {rest_of_sentence}"
            else:
                complex_sentence += f", while {subject} {rest_of_sentence}"
        return complex_sentence + "."
    else:
        return sentences[0]

