from dataclasses import dataclass

@dataclass
class Triple:
    subject: str
    predicate: str
    object: str

# EVOLVE-BLOCK-START

def predict(triples: list[Triple]) -> str:
    subject = triples[0].subject
    sentence = f"{subject} is an artist who "

    associated_bands = []
    genres = []
    instruments = []
    origins = []
    birth_dates = []
    active_years = []
    occupations = []
    record_labels = []
    backgrounds = []

    for triple in triples:
        if triple.subject == subject:
            if triple.predicate == "associatedBand/associatedMusicalArtist":
                associated_bands.append(triple.object)
            elif triple.predicate == "genre":
                genres.append(triple.object)
            elif triple.predicate == "instrument":
                instruments.append(triple.object)
            elif triple.predicate == "origin":
                origins.append(triple.object)
            elif triple.predicate == "birthDate" or triple.predicate == "birthYear":
                birth_dates.append(triple.object)
            elif triple.predicate == "activeYearsStartYear" or triple.predicate == "activeYearsEndYear":
                active_years.append(triple.object)
            elif triple.predicate == "occupation":
                occupations.append(triple.object)
            elif triple.predicate == "recordLabel":
                record_labels.append(triple.object)
            elif triple.predicate == "background":
                backgrounds.append(triple.object)

    if associated_bands:
        sentence += f"is associated with the bands {', '.join(associated_bands)}, "
    if genres:
        sentence += f"plays {', '.join(genres)} music, "
    if instruments:
        sentence += f"plays the {', '.join(instruments)}, "
    if origins:
        sentence += f"is from {', '.join(origins)}, "
    if birth_dates:
        sentence += f"was born in {', '.join(birth_dates)}, "
    if active_years:
        sentence += f"was active from {', '.join(active_years)} years, "
    if occupations:
        sentence += f"works as a {', '.join(occupations)}, "
    if record_labels:
        sentence += f"is signed to {', '.join(record_labels)} records, "
    if backgrounds:
        sentence += f"has a background as a {', '.join(backgrounds)}, "

    # Remove trailing comma and space
    sentence = sentence.rstrip(', ') + "."

    return sentence

