def predict(triples: list[Triple]) -> str:
    subjects = {}
    for triple in triples:
        if triple.subject not in subjects:
            subjects[triple.subject] = []
        subjects[triple.subject].append((triple.predicate, triple.object))

    sentences = []
    descriptions = []
    main_subject = None
    for subject, predicates in subjects.items():
        if not main_subject:
            main_subject = subject
        for predicate, obj in predicates:
            if predicate == "associatedBand/associatedMusicalArtist":
                descriptions.append((f"{subject} is associated with the band {obj}", subject))
            elif predicate == "genre":
                descriptions.append((f"{subject} performs in the {obj} genre", subject))
            elif predicate == "instrument":
                descriptions.append((f"{subject} plays the {obj}", subject))
            elif predicate == "occupation":
                descriptions.append((f"{subject} works as a {obj}", subject))
            elif predicate == "background":
                descriptions.append((f"{subject} has a background as a {obj}", subject))
            elif predicate == "birthPlace":
                descriptions.append((f"{subject} was born in {obj}", subject))
            elif predicate == "birthDate":
                descriptions.append((f"{subject} was born on {obj}", subject))
            elif predicate == "activeYearsStartYear":
                descriptions.append((f"{subject} started their career in {obj}", subject))
            elif predicate == "activeYearsEndYear":
                descriptions.append((f"{subject} ended their career in {obj}", subject))
            elif predicate == "recordLabel":
                descriptions.append((f"{subject} is signed to {obj} records", subject))
            elif predicate == "origin":
                descriptions.append((f"{subject} is from {obj}", subject))
            elif predicate == "language":
                descriptions.append((f"{subject} speaks {obj}", subject))
            else:
                descriptions.append((f"{subject} has a {predicate} of {obj}", subject))

    complex_sentence = f"{main_subject} "
    subject_descriptions = {}
    for description, subject in descriptions:
        if subject not in subject_descriptions:
            subject_descriptions[subject] = []
        subject_descriptions[subject].append(description)

    for subject, descriptions in subject_descriptions.items():
        if subject == main_subject:
            complex_sentence += f"who {', '.join(descriptions)}"
        else:
            complex_sentence += f", and is also associated with {subject} who {', '.join(descriptions)}"

    return complex_sentence + "."
