from dataclasses import dataclass

@dataclass
class Triple:
    subject: str
    predicate: str
    object: str

# EVOLVE-BLOCK-START

def predict(triples: list[Triple]) -> str:
    subjects = {}
    for triple in triples:
        if triple.subject not in subjects:
            subjects[triple.subject] = []
        subjects[triple.subject].append((triple.predicate, triple.object))

    sentences = []
    descriptions = {}
    for subject, predicates in subjects.items():
        descriptions[subject] = []
        for predicate, obj in predicates:
            if predicate == "associatedBand/associatedMusicalArtist":
                descriptions[subject].append(f"associated with the band {obj}")
            elif predicate == "genre":
                descriptions[subject].append(f"performs in the {obj} genre")
            elif predicate == "instrument":
                descriptions[subject].append(f"plays the {obj}")
            elif predicate == "occupation":
                descriptions[subject].append(f"works as a {obj}")
            elif predicate == "background":
                descriptions[subject].append(f"has a background as a {obj}")
            elif predicate == "birthPlace":
                descriptions[subject].append(f"was born in {obj}")
            elif predicate == "birthYear":
                descriptions[subject].append(f"was born in {obj}")
            elif predicate == "activeYearsStartYear":
                descriptions[subject].append(f"started their career in {obj}")
            elif predicate == "activeYearsEndYear":
                descriptions[subject].append(f"ended their career in {obj}")
            elif predicate == "stylisticOrigin":
                descriptions[subject].append(f"has its origins in {obj}")
            else:
                descriptions[subject].append(f"has a {predicate} of {obj}")

    # Try to create a complex sentence from all the given triples
    if len(descriptions) > 1:
        complex_sentence = f"{list(descriptions.keys())[0]} "
        for subject, description in descriptions.items():
            if subject != list(descriptions.keys())[0]:
                complex_sentence += f"who is associated with {subject} that "
            complex_sentence += f"{', '.join(description)}"
            if subject != list(descriptions.keys())[-1]:
                complex_sentence += ", "
        return complex_sentence + "."
    else:
        for subject, description in descriptions.items():
            if "associatedBand/associatedMusicalArtist" in [x[0] for x in subjects[subject]]:
                associated_bands = [x[1] for x in subjects[subject] if x[0] == "associatedBand/associatedMusicalArtist"]
                return f"{subject} is a musician who is associated with the bands {', '.join(associated_bands)} and {', '.join([x[1] for x in subjects[subject] if x[0] != 'associatedBand/associatedMusicalArtist'])}."
            else:
                return f"{subject} is a musician who {', '.join(description)}."
