def predict(triples: list[Triple]) -> str:
    subjects = {}
    for triple in triples:
        if triple.subject not in subjects:
            subjects[triple.subject] = []
        subjects[triple.subject].append((triple.predicate, triple.object))

    sentences = []
    for subject, predicates in subjects.items():
        sentence = f"{subject} "
        descriptions = []
        birth_info = []
        career_info = []
        location_info = []
        genre_info = []
        instrument_info = []
        other_info = []

        for predicate, obj in predicates:
            if predicate == "birthPlace":
                birth_info.append(f"born in {obj}")
            elif predicate == "birthDate":
                birth_info.append(f"born on {obj}")
            elif predicate == "activeYearsStartYear":
                career_info.append(f"started their career in {obj}")
            elif predicate == "activeYearsEndYear":
                career_info.append(f"ended their career in {obj}")
            elif predicate == "country":
                location_info.append(f"from {obj}")
            elif predicate == "nationality":
                location_info.append(f"of {obj} nationality")
            elif predicate == "genre":
                genre_info.append(f"performs in the {obj} genre")
            elif predicate == "instrument":
                instrument_info.append(f"plays the {obj}")
            elif predicate == "associatedBand/associatedMusicalArtist":
                other_info.append(f"associated with the band {obj}")
            elif predicate == "recordLabel":
                other_info.append(f"signed to {obj} records")
            elif predicate == "occupation":
                other_info.append(f"works as a {obj}")
            elif predicate == "background":
                other_info.append(f"has a background as a {obj}")
            elif predicate == "leaderTitle":
                other_info.append(f"has a leader title of {obj}")
            elif predicate == "leader":
                other_info.append(f"has a leader named {obj}")
            elif predicate == "deathDate":
                other_info.append(f"died on {obj}")
            elif predicate == "deathPlace":
                other_info.append(f"died in {obj}")
            else:
                other_info.append(f"has a {predicate} of {obj}")

        if birth_info:
            descriptions.append(f" who {', '.join(birth_info)}")
        if career_info:
            descriptions.append(f" who {', '.join(career_info)}")
        if location_info:
            descriptions.append(f" who {', '.join(location_info)}")
        if genre_info:
            descriptions.append(f" who {', '.join(genre_info)}")
        if instrument_info:
            descriptions.append(f" who {', '.join(instrument_info)}")
        if other_info:
            descriptions.append(f" who {', '.join(other_info)}")

        if descriptions:
            sentence += f"{''.join(descriptions)}."
        else:
            sentence += "."

        sentences.append(sentence)

    # Try to create a complex sentence from all the given triples
    if len(sentences) > 1:
        complex_sentence = sentences[0].replace('.', '')
        for sentence in sentences[1:]:
            subject = sentence.split(' ')[0]
            rest_of_sentence = ' '.join(sentence.split(' ')[1:]).replace('.', '')
            if subject in complex_sentence:
                complex_sentence += f", and is also known as {rest_of_sentence}"
            else:
                complex_sentence += f", and {subject} {rest_of_sentence}"
        return complex_sentence + "."
    else:
        return sentences[0]

