from dataclasses import dataclass

@dataclass
class Triple:
    subject: str
    predicate: str
    object: str

# EVOLVE-BLOCK-START

def predict(triples: list[Triple]) -> str:
    subjects = {}
    for triple in triples:
        if triple.subject not in subjects:
            subjects[triple.subject] = []
        subjects[triple.subject].append((triple.predicate, triple.object))

    sentences = []
    for subject, predicates in subjects.items():
        sentence = f"{subject} "
        descriptions = []
        for predicate, obj in predicates:
            if predicate == "genre":
                descriptions.append(f"a {obj} musician")
            elif predicate == "instrument":
                descriptions.append(f"who plays the {obj}")
            elif predicate == "occupation":
                descriptions.append(f"and works as a {obj}")
            elif predicate == "background":
                descriptions.append(f"with a background as {obj}")
            elif predicate == "birthPlace":
                descriptions.append(f"born in {obj}")
            elif predicate == "associatedBand/associatedMusicalArtist":
                descriptions.append(f"associated with the band {obj}")
            elif predicate == "bandMember":
                descriptions.append(f"member of the band {obj}")
            elif predicate == "activeYearsStartYear":
                descriptions.append(f"active since {obj}")
            elif predicate == "activeYearsEndYear":
                descriptions.append(f"active until {obj}")
            elif predicate == "birthDate":
                descriptions.append(f"born on {obj}")
            elif predicate == "birthYear":
                descriptions.append(f"born in {obj}")

        if descriptions:
            sentence += f"{' and '.join(descriptions)}"

        sentences.append(sentence)

    # Try to create a complex sentence from all the given triples
    if len(sentences) > 1:
        complex_sentence = sentences[0]
        for sentence in sentences[1:]:
            complex_sentence += f", {sentence}"
        return complex_sentence + "."
    else:
        return sentences[0] + "."

