        sentence = f"{subject} "
        associated_bands = []
        genres = []
        instruments = []
        occupations = []
        backgrounds = []
        birth_places = []
        other_predicates = []
        birth_years = []
        active_years_start = []
        active_years_end = []
        record_labels = []
        locations = []

        for predicate, obj in predicates:
            if predicate == "associatedBand/associatedMusicalArtist":
                associated_bands.append(obj)
