                active_years_start.append(obj)
            elif predicate == "activeYearsEndYear":
                active_years_end.append(obj)
            elif predicate == "recordLabel":
                record_labels.append(obj)
            elif predicate == "location":
                locations.append(obj)
            else:
                other_predicates.append((predicate, obj))

        if genres:
            sentence += f"is a {', '.join(genres)} musician"
        if instruments:
            sentence += f" who plays the {', '.join(instruments)}"
        if occupations:
            sentence += f" and works as a {', '.join(occupations)}"
        if backgrounds:
            sentence += f" with a background as {', '.join(backgrounds)}"
        if birth_places:
            sentence += f", born in {', '.join(birth_places)}"
        if birth_years:
            sentence += f", born in {', '.join(birth_years)}"
        if active_years_start:
            sentence += f", active since {', '.join(active_years_start)}"
        if active_years_end:
            sentence += f", active until {', '.join(active_years_end)}"
        if associated_bands:
            sentence += f", and is associated with the bands {', '.join(associated_bands)}"
        if record_labels:
            sentence += f", and is signed to {', '.join(record_labels)} records"
        if locations:
            sentence += f", from {', '.join(locations)}"
        for predicate, obj in other_predicates:
            sentence += f", with a {predicate} of {obj}"

        sentences.append(sentence)

    # Try to create a complex sentence from all the given triples
    if len(sentences) > 1:
        complex_sentence = sentences[0].replace('.', '')
        subjects = [sentence.split(' ')[0] for sentence in sentences]
        for i, sentence in enumerate(sentences[1:]):
            subject = sentence.split(' ')[0]
            rest_of_sentence = ' '.join(sentence.split(' ')[1:]).replace('.', '')
            if subject in complex_sentence:
                complex_sentence += f", and is also associated with {rest_of_sentence}"
            elif subject in subjects[:i]:
                complex_sentence += f", and similarly, {subject} {rest_of_sentence}"
            else:
                complex_sentence += f", meanwhile, {subject} {rest_of_sentence}"
        return complex_sentence + "."
    else:
        # Handle the case with a single sentence by making it more descriptive
        if len(triples) == 1:
            triple = triples[0]
            if triple.predicate == "genre":
                return f"{triple.subject} is a renowned musician known for their exceptional talent in the {triple.object} genre."
            elif triple.predicate == "instrument":
                return f"{triple.subject} is a skilled musician who expertly plays the {triple.object}."
            else:
                return sentences[0]
        else:
            # For multiple triples with the same subject, combine them into a single sentence
            subject = triples[0].subject
            predicates = [triple.predicate for triple in triples]
            objects = [triple.object for triple in triples]
            sentence = f"{subject} is a musician who "
