def predict(triples: list[Triple]) -> str:
    subject = triples[0].subject
    predicates = {}

    for triple in triples:
        if triple.subject == subject:
            predicates[triple.predicate] = triple.object
        elif triple.object == subject:
            # Handle cases where the subject is the object of another triple
            predicates[triple.predicate + "_of"] = triple.subject

    sentence = f"{subject} is a "

    # Prioritize certain predicates for better sentence structure
    prioritized_predicates = ["genre", "instrument", "occupation", "background"]
    other_predicates = [predicate for predicate in predicates if predicate not in prioritized_predicates]

    for predicate in prioritized_predicates:
        if predicate in predicates:
            if predicate == "genre":
                sentence += f"{predicates[predicate]} musician"
            elif predicate == "instrument":
                sentence += f"{predicates[predicate]} player"
            elif predicate == "occupation":
                sentence += f"{predicates[predicate]}"
            elif predicate == "background":
                sentence += f"{predicates[predicate]} {subject}"
            sentence += ", "

    # Add other predicates
    for predicate in other_predicates:
        if predicate.endswith("_of"):
            sentence += f"associated with {predicates[predicate]} as their {predicate[:-3]}, "
        else:
            sentence += f"with a {predicate} of {predicates[predicate]}, "

    # Remove trailing comma and space, and add a period
    sentence = sentence.rstrip(', ') + "."

    return sentence

