def predict(triples: list[Triple]) -> str:
    subjects = {}
    for triple in triples:
        if triple.subject not in subjects:
            subjects[triple.subject] = []
        subjects[triple.subject].append((triple.predicate, triple.object))

    sentences = []
    for subject, predicates in subjects.items():
        sentence = f"{subject} "
        descriptions = []
        relations = []

        for predicate, obj in predicates:
            if predicate in ["associatedBand/associatedMusicalArtist", "genre", "instrument", "occupation", "background", "birthPlace", "birthYear", "activeYearsStartYear", "activeYearsEndYear"]:
                descriptions.append((predicate, obj))
            else:
                relations.append((predicate, obj))

        if descriptions:
            description_sentence = f"who "
            for predicate, obj in descriptions:
                if predicate == "genre":
                    description_sentence += f"performs {obj} music, "
                elif predicate == "instrument":
                    description_sentence += f"plays the {obj}, "
                elif predicate == "associatedBand/associatedMusicalArtist":
                    description_sentence += f"is associated with the band {obj}, "
                elif predicate == "occupation":
                    description_sentence += f"works as a {obj}, "
                elif predicate == "background":
                    description_sentence += f"has a background as a {obj}, "
                elif predicate == "birthPlace":
                    description_sentence += f"was born in {obj}, "
                elif predicate == "birthYear":
                    description_sentence += f"was born in {obj}, "
                elif predicate == "activeYearsStartYear":
                    description_sentence += f"started their career in {obj}, "
                elif predicate == "activeYearsEndYear":
                    description_sentence += f"ended their career in {obj}, "
            description_sentence = description_sentence[:-2]  # Remove the last comma and space
            sentence += description_sentence

        if relations:
            relation_sentence = f" and "
            for predicate, obj in relations:
                if predicate == "deathPlace":
                    relation_sentence += f"died in {obj}, "
                elif predicate == "leader":
                    relation_sentence += f"has a leader named {obj}, "
                elif predicate == "capital":
                    relation_sentence += f"has a capital in {obj}, "
                else:
                    relation_sentence += f"has a {predicate} of {obj}, "
            relation_sentence = relation_sentence[:-2]  # Remove the last comma and space
            sentence += relation_sentence

        sentence += "."
        sentences.append(sentence)

    # Try to create a complex sentence from all the given triples
    if len(sentences) > 1:
        complex_sentence = sentences[0]
        for sentence in sentences[1:]:
            subject = sentence.split(' ')[0]
            rest_of_sentence = ' '.join(sentence.split(' ')[1:])
            complex_sentence += f" Additionally, {subject} {rest_of_sentence}"
        return complex_sentence
    else:
        return sentences[0]
