            subjects[triple.subject] = []
        subjects[triple.subject].append((triple.predicate, triple.object))

    sentences = []
    for subject, predicates in subjects.items():
        sentence = f"{subject} "
        personal_info = []
        musical_career = []
        associations = []
        other_info = []

        for predicate, obj in predicates:
            if predicate in ["birthPlace", "birthDate", "nationality"]:
                personal_info.append((predicate, obj))
            elif predicate in ["genre", "instrument", "occupation", "background"]:
                musical_career.append((predicate, obj))
            elif predicate in ["associatedBand/associatedMusicalArtist", "recordLabel"]:
                associations.append((predicate, obj))
            else:
                other_info.append((predicate, obj))

        descriptions = []
        if personal_info:
            personal_desc = ", ".join([f"{obj}" if predicate == "birthPlace" else f"born on {obj}" if predicate == "birthDate" else f"{obj} nationality" for predicate, obj in personal_info])
            descriptions.append(f"with personal details: {personal_desc}")
        if musical_career:
            career_desc = ", ".join([f"performs {obj}" if predicate == "genre" else f"plays {obj}" if predicate == "instrument" else f"works as a {obj}" if predicate == "occupation" else f"has a background as {obj}" for predicate, obj in musical_career])
            descriptions.append(f"and a musical career involving {career_desc}")
        if associations:
            assoc_desc = ", ".join([f"associated with {obj}" if predicate == "associatedBand/associatedMusicalArtist" else f"signed to {obj} records" for predicate, obj in associations])
            descriptions.append(f"including associations with {assoc_desc}")
        for predicate, obj in other_info:
            descriptions.append(f"and has a {predicate} of {obj}")

        if descriptions:
            sentence += f" who {', '.join(descriptions)}."
        else:
            sentence += "."

        sentences.append(sentence)

    # Try to create a complex sentence from all the given triples
    if len(sentences) > 1:
        complex_sentence = sentences[0].replace('.', '')
        for sentence in sentences[1:]:
            subject = sentence.split(' ')[0]
            rest_of_sentence = ' '.join(sentence.split(' ')[1:]).replace('.', '')
            if subject in complex_sentence:
                if "who" in complex_sentence:
                    complex_sentence += f", and also {rest_of_sentence}"
                else:
                    complex_sentence += f" who {rest_of_sentence}"
            else:
                if any(word in rest_of_sentence for word in ["of", "in", "from"]):
                    complex_sentence += f", which is {rest_of_sentence}"
                else:
                    complex_sentence += f", while {subject} {rest_of_sentence}"
        return complex_sentence + "."
    else:
        return sentences[0]

