def predict(triples: list[Triple]) -> str:
    subject = triples[0].subject
    predicates = {}

    for triple in triples:
        if triple.subject == subject:
            if triple.predicate in predicates:
                if not isinstance(predicates[triple.predicate], list):
                    predicates[triple.predicate] = [predicates[triple.predicate]]
                predicates[triple.predicate].append(triple.object)
            else:
                predicates[triple.predicate] = triple.object
        elif triple.object == subject:
            # Handle cases where the subject is the object of another triple
            predicates[triple.predicate + "_of"] = triple.subject

    sentence = f"{subject} is a "

    # Prioritize certain predicates for better sentence structure
    prioritized_predicates = ["genre", "instrument", "occupation", "background"]
    other_predicates = [predicate for predicate in predicates if predicate not in prioritized_predicates]

    for predicate in prioritized_predicates:
        if predicate in predicates:
            if predicate == "genre":
                if isinstance(predicates[predicate], list):
                    sentence += f"a {', '.join(predicates[predicate])} musician"
                else:
                    sentence += f"a {predicates[predicate]} musician"
            elif predicate == "instrument":
                if isinstance(predicates[predicate], list):
                    sentence += f"a {', '.join(predicates[predicate])} player"
                else:
                    sentence += f"a {predicates[predicate]} player"
            elif predicate == "occupation":
                if isinstance(predicates[predicate], list):
                    sentence += f"{', '.join(predicates[predicate])}"
                else:
                    sentence += f"{predicates[predicate]}"
            elif predicate == "background":
                if isinstance(predicates[predicate], list):
                    sentence += f"with a background in {', '.join(predicates[predicate])}"
                else:
                    sentence += f"with a background as a {predicates[predicate]}"
            sentence += ", "

    # Add other predicates
    for predicate in other_predicates:
        if predicate.endswith("_of"):
            sentence += f"associated with {predicates[predicate]} as their {predicate[:-3]}, "
        elif predicate == "associatedBand/associatedMusicalArtist":
            if isinstance(predicates[predicate], list):
                sentence += f"and has been associated with the bands {', '.join(predicates[predicate])}, "
            else:
                sentence += f"and is associated with the band {predicates[predicate]}, "
        elif predicate == "activeYearsStartYear" or predicate == "activeYearsEndYear":
            sentence += f"active from {predicates[predicate]} years, "
        elif predicate == "recordLabel":
            sentence += f"and is signed to {predicates[predicate]} records, "
        else:
            sentence += f"with a {predicate} of {predicates[predicate]}, "

    # Remove trailing comma and space, and add a period
    sentence = sentence.rstrip(', ') + "."

    return sentence

