from dataclasses import dataclass

@dataclass
class Triple:
    subject: str
    predicate: str
    object: str

# EVOLVE-BLOCK-START

def predict(triples: list[Triple]) -> str:
    subjects = {}
    for triple in triples:
        if triple.subject not in subjects:
            subjects[triple.subject] = []
        subjects[triple.subject].append((triple.predicate, triple.object))

    sentences = []
    for subject, predicates in subjects.items():
        sentence = f"{subject} is a musician who "
        descriptions = []
        genres = []
        instruments = []
        occupations = []
        backgrounds = []
        birth_places = []
        birth_dates = []
        active_years_start = []
        active_years_end = []
        associated_bands = []
        record_labels = []
        other_predicates = []

        for predicate, obj in predicates:
            if predicate == "associatedBand/associatedMusicalArtist":
                associated_bands.append(obj)
            elif predicate == "genre":
                genres.append(obj)
            elif predicate == "instrument":
                instruments.append(obj)
            elif predicate == "occupation":
                occupations.append(obj)
            elif predicate == "background":
                backgrounds.append(obj)
            elif predicate == "birthPlace":
                birth_places.append(obj)
            elif predicate == "birthDate":
                birth_dates.append(obj)
            elif predicate == "activeYearsStartYear":
                active_years_start.append(obj)
            elif predicate == "activeYearsEndYear":
                active_years_end.append(obj)
            elif predicate == "recordLabel":
                record_labels.append(obj)
            else:
                other_predicates.append((predicate, obj))

        if genres:
            descriptions.append(f"performs in the {', '.join(genres)} genre")
        if instruments:
            descriptions.append(f"plays the {', '.join(instruments)}")
        if occupations:
            descriptions.append(f"works as a {', '.join(occupations)}")
        if backgrounds:
            descriptions.append(f"has a background as a {', '.join(backgrounds)}")
        if birth_places:
            descriptions.append(f"was born in {', '.join(birth_places)}")
        if birth_dates:
            descriptions.append(f"was born on {', '.join(birth_dates)}")
        if active_years_start:
            descriptions.append(f"started their career in {', '.join(active_years_start)}")
        if active_years_end:
            descriptions.append(f"ended their career in {', '.join(active_years_end)}")
        if associated_bands:
            descriptions.append(f"is associated with the bands {', '.join(associated_bands)}")
        if record_labels:
            descriptions.append(f"is signed to {', '.join(record_labels)} records")
        for predicate, obj in other_predicates:
            descriptions.append(f"has a {predicate} of {obj}")

        if descriptions:
            sentence += ' and '.join(descriptions) + "."
        else:
            sentence += "."

        sentences.append(sentence)

    # Try to create a complex sentence from all the given triples
    if len(sentences) > 1:
        complex_sentence = sentences[0]
        for sentence in sentences[1:]:
            subject = sentence.split(' ')[0]
            rest_of_sentence = ' '.join(sentence.split(' ')[1:]).replace('.', '')
            if subject in complex_sentence:
                complex_sentence += f", who is also {rest_of_sentence}"
            else:
                complex_sentence += f", and {subject} {rest_of_sentence}"
        return complex_sentence
    else:
        main_subject = sentences[0].split(' ')[0]
        main_sentence = ' '.join(sentences[0].split(' ')[1:]).replace('.', '')
        for sentence in triples:
            if sentence.subject != main_subject:
                subject = sentence.subject
                predicate = sentence.predicate
                obj = sentence.object
                if predicate == "stylisticOrigin":
                    complex_sentence = f"{main_subject} {main_sentence}, which originated from {obj}."
                elif predicate == "derivative":
                    complex_sentence = f"{main_subject} {main_sentence}, which gave rise to {obj}."
                else:
                    complex_sentence = f"{main_subject} {main_sentence}, and {subject} has a {predicate} of {obj}."
                return complex_sentence
        return sentences[0]

