from dataclasses import dataclass

@dataclass
class Triple:
    subject: str
    predicate: str
    object: str

# EVOLVE-BLOCK-START

def predict(triples: list[Triple]) -> str:
    subjects = {}
    for triple in triples:
        if triple.subject not in subjects:
            subjects[triple.subject] = []
        subjects[triple.subject].append((triple.predicate, triple.object))

    sentences = []
    descriptions = []
    for subject, predicates in subjects.items():
        for predicate, obj in predicates:
            if predicate == "associatedBand/associatedMusicalArtist":
                descriptions.append(f"{subject} is associated with the band {obj}")
            elif predicate == "genre":
                descriptions.append(f"{subject} performs in the {obj} genre")
            elif predicate == "instrument":
                descriptions.append(f"{subject} plays the {obj}")
            elif predicate == "occupation":
                descriptions.append(f"{subject} works as a {obj}")
            elif predicate == "background":
                descriptions.append(f"{subject} has a background as a {obj}")
            elif predicate == "birthPlace":
                descriptions.append(f"{subject} was born in {obj}")
            elif predicate == "birthDate":
                descriptions.append(f"{subject} was born on {obj}")
            elif predicate == "activeYearsStartYear":
                descriptions.append(f"{subject} started their career in {obj}")
            elif predicate == "activeYearsEndYear":
                descriptions.append(f"{subject} ended their career in {obj}")
            elif predicate == "recordLabel":
                descriptions.append(f"{subject} is signed to {obj} records")
            else:
                descriptions.append(f"{subject} has a {predicate} of {obj}")

    complex_sentence = ""
    for description in descriptions:
        if complex_sentence == "":
            complex_sentence += description
        else:
            subject = description.split(' ')[0]
            rest_of_description = ' '.join(description.split(' ')[1:])
            if subject in complex_sentence:
                complex_sentence += f", and {rest_of_description}"
            else:
                complex_sentence += f", while {description}"

    return complex_sentence + "."

