from dataclasses import dataclass

@dataclass
class Triple:
    subject: str
    predicate: str
    object: str

# EVOLVE-BLOCK-START

def predict(triples: list[Triple]) -> str:
    subjects = {}
    for triple in triples:
        if triple.subject not in subjects:
            subjects[triple.subject] = {}
        subjects[triple.subject][triple.predicate] = triple.object

    sentences = []
    for subject, predicates in subjects.items():
        sentence = f"{subject} is a "
        first = True
        for predicate, obj in predicates.items():
            if predicate == "genre":
                if first:
                    sentence += f"{obj} musician"
                    first = False
                else:
                    sentence += f", and also performs {obj} music"
            elif predicate == "instrument":
                if first:
                    sentence += f"{obj} player"
                    first = False
                else:
                    sentence += f", and plays the {obj}"
            elif predicate == "occupation":
                if first:
                    sentence += f"{obj}"
                    first = False
                else:
                    sentence += f", with an occupation as {obj}"
            elif predicate == "background":
                if first:
                    sentence += f"{obj} {subject}"
                    first = False
                else:
                    sentence += f", and has a background as {obj}"
            elif predicate == "birthPlace":
                sentence += f", born in {obj}"
            elif predicate == "associatedBand/associatedMusicalArtist":
                sentence += f", associated with the band {obj}"
            elif predicate == "stylisticOrigin":
                sentence += f", which originated from {obj}"
            elif predicate == "derivative":
                sentence += f", and is a derivative of {obj}"
            else:
                sentence += f", with a {predicate} of {obj}"
        sentences.append(sentence)

    # Create a dictionary to store the sentences for each subject
    subject_sentences = {}
    for triple in triples:
        subject = triple.subject
        if subject not in subject_sentences:
            subject_sentences[subject] = []
        # Create a sentence for the current triple
        if triple.predicate == "genre":
            sentence = f"{triple.subject} plays {triple.object} music"
        elif triple.predicate == "instrument":
            sentence = f"{triple.subject} plays the {triple.object}"
        elif triple.predicate == "occupation":
            sentence = f"{triple.subject} is a {triple.object}"
        elif triple.predicate == "background":
            sentence = f"{triple.subject} has a background as {triple.object}"
        elif triple.predicate == "birthPlace":
            sentence = f"{triple.subject} was born in {triple.object}"
        elif triple.predicate == "associatedBand/associatedMusicalArtist":
            sentence = f"{triple.subject} is associated with the band {triple.object}"
        else:
            sentence = f"{triple.subject} has a {triple.predicate} of {triple.object}"
        subject_sentences[subject].append(sentence)

    # Combine the sentences for each subject
    combined_sentences = []
    for subject, sentences in subject_sentences.items():
        combined_sentence = sentences[0]
        for sentence in sentences[1:]:
            combined_sentence += f", and {sentence}"
        combined_sentences.append(combined_sentence)

    # Combine the sentences for related subjects
    related_sentences = {}
    for triple in triples:
        if triple.predicate == "associatedBand/associatedMusicalArtist":
            band = triple.object
            for sentence in combined_sentences:
                if band in sentence:
                    subject = triple.subject
                    if subject in related_sentences and sentence != related_sentences[subject]:
                        related_sentences[subject] = related_sentences[subject] + f", and is also associated with {sentence.split('associated with the band')[1]}"
    for subject, sentence in related_sentences.items():
        combined_sentences[combined_sentences.index(related_sentences[subject].split(', and is also associated with')[0])] = sentence

    # Return the combined sentences
    return '. '.join(combined_sentences) + '.'

